/**
 * @file test/module-city_search-test.js
 * @description Returns an array of all cities matching your search, 
 * and their basic information. Includes a "Key" for that city 
 * that can be used to get forecast data. 
 *
 * Generated by the converse-cli tool for use with the Converse AI
 * Plugins SDK. https://developers.converse.ai/
 */

const request     = require('supertest');
const expect      = require('chai').expect;
const server      = require('./lib/express');

describe('Search for City', function () {

  it('base', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'city_search',
          moduleParam: {
            search_text: undefined
          },
          registrationData: {
            api_key: undefined
          }        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        done();
      });
  })

  it('Bellevue Search', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'city_search',
          moduleParam: {
            search_text: 'Bellevue, WA'
          },
          registrationData: {
            api_key: 'UGt1WSU72oKw0XcVEetB5LD4GypzL7Ka'
          }        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        done();
      });
  })

});
